	('7b8b1169d904', 'main', 0, NULL, '2023-08-08 08:19:25', '2023-11-12 14:43:37', '8152b8296000', '3e3b8d27c9d9', 'cfccaecd4bad', 'storyboard', '{"sections": {"02980805-ac83-4b9f-adeb-24e6a2fd04fe": {"column1": "", "column3": "", "position": 2, "column2Html": "<p>Wir nahmen den 10er in Zürich Bhf Oerlikon Ost richtung Flughafen um 18:31 ankunft 1844 in zürich Flughafen</p>"}, "be727180-fa4a-4c06-900d-454b2b18d9bb": {"column1": "", "column3": "", "position": 3, "column2Html": "<p>18:49 BUS 524 8063 Richtung Oberembrach, Dorf bis nach Lufigen Augwil. Ankunft 18:58</p>"}, "dcc46474-1d92-4010-93b3-d42f966b34dd": {"column1": "", "column3": "", "position": 4, "column2Html": "<p>Wir laufen zum Lagerplatz</p>"}, "effa3fa5-cd07-4e82-a8aa-198ed9a7c1d4": {"column1": "", "column3": "", "position": 1, "column2Html": "<p>Wir treffen uns um 18:00 am Bahnhof Oerlikon.</p>"}}}'),
